mapper.select:
   SELECT (SELECT COUNT(1) FROM gravida_info g JOIN resident_base_info r ON g.archive_no = r.archive_no
   where 1 = 1
	
		and province_code = #{provinceCode,jdbcType=VARCHAR}
	
	
		and city_code = #{cityCode,jdbcType=VARCHAR}
	
	
		and county_code = #{countyCode,jdbcType=VARCHAR}
	
	
		and towns_code = #{townsCode,jdbcType=VARCHAR}
	
	
		and village_code = #{villageCode,jdbcType=VARCHAR}
	
   ) AS gravidaInfoNum,
	(SELECT COUNT(1) FROM gravida_after_record a JOIN resident_base_info r ON a.archive_no = r.archive_no 
	WHERE a.record_type = 2
	
		and province_code = #{provinceCode,jdbcType=VARCHAR}
	
	
		and city_code = #{cityCode,jdbcType=VARCHAR}
	
	
		and county_code = #{countyCode,jdbcType=VARCHAR}
	
	
		and towns_code = #{townsCode,jdbcType=VARCHAR}
	
	
		and village_code = #{villageCode,jdbcType=VARCHAR}
	
	
            and DATE_FORMAT(visit_date, '%Y-%m-%d') >= DATE_FORMAT(#{startTime}, '%Y-%m-%d')
	
	
            and DATE_FORMAT(visit_date, '%Y-%m-%d') <= DATE_FORMAT(#{endTime}, '%Y-%m-%d')
	
	) AS gravidaAfterNum

select 

from gravida_info
where 1 = 1
 
   and  archive_no = #{archiveNo,jdbcType=VARCHAR}
   
 
   and  exists(select 1 from resident_base_info i where i.archive_no = archive_no and  i.province_code = #{provinceCode,jdbcType=VARCHAR})
    
 
   and  exists(select 1 from resident_base_info i where i.archive_no = archive_no and  i.city_code = #{cityCode,jdbcType=VARCHAR})
  
 
   and  exists(select 1 from resident_base_info i where i.archive_no = archive_no and  i.county_code = #{countyCode,jdbcType=VARCHAR})
   
 
   and  exists(select 1 from resident_base_info i where i.archive_no = archive_no and  i.towns_code = #{townsCode,jdbcType=VARCHAR})
   
 
   and  exists(select 1 from resident_base_info i where i.archive_no = archive_no and  i.village_code = #{villageCode,jdbcType=VARCHAR})
    
 
   and (name like concat(concat('%',#{name,jdbcType=VARCHAR}),'%') 
   or id_number like concat(concat('%',#{name,jdbcType=VARCHAR}),'%'))
   
 
   and  status = #{status,jdbcType=VARCHAR}
  
 
   and  gravida_age >= #{minage,jdbcType=INTEGER}
   
 
   and  gravida_age <= #{maxage,jdbcType=INTEGER}
    
 
   and  visit_date >= #{startDate,jdbcType=INTEGER}
   
 
   and  visit_date <= #{endDate,jdbcType=INTEGER}
